const footerApp = document.getElementById('root');

// copyright
const footerDiv = document.createElement('div');
footerDiv.setAttribute('class' , 'footer');
var design = document.createTextNode("Created by : ");
var a = document.createElement('a');  
a.setAttribute('id' , 'copyright');
var link = document.createTextNode(design.textContent+" Sanjula De Alwis");
a.href = "https://www.linkedin.com/in/sanjuladealwis/"; 
footerDiv.appendChild(a);
a.appendChild(link);

//social icons

const socialIcons = document.createElement('div');
socialIcons.setAttribute('class' , 'social-icons');

const FbIcon = document.createElement('span');
FbIcon.setAttribute('class' , 'facebook-icon icon');

const IgIcon = document.createElement('span');
IgIcon.setAttribute('class' , 'Instagram-icon icon');

const LiIcon = document.createElement('span');
LiIcon.setAttribute('class' , 'Linkedin-icon icon');

const GitIcon = document.createElement('span');
GitIcon.setAttribute('class' , 'GitHub-icon icon');

//Facebook Icon
var linkFbClass = document.createElement('a');
linkFbClass.href = 'https://www.facebook.com/sanjula.dealwis/';
var LinkFb = document.createElement('i');
LinkFb.setAttribute('class' ,'fa fa-facebook');
FbIcon.appendChild(linkFbClass);

socialIcons.appendChild(FbIcon);
FbIcon.appendChild(linkFbClass);
linkFbClass.appendChild(LinkFb);

//Instagram Icon
var linkIgClass = document.createElement('a');
linkIgClass.href = 'https://www.instagram.com/sanjulaaa_/';
var LinkIg = document.createElement('i');
LinkIg.setAttribute('class', 'fa fa-instagram');
IgIcon.appendChild(linkIgClass);

socialIcons.appendChild(IgIcon);
IgIcon.appendChild(linkIgClass);
linkIgClass.appendChild(LinkIg);

//Linkedin Icon
var linkLiClass = document.createElement('a');
linkLiClass.href = 'https://www.linkedin.com/in/sanjuladealwis/';
var LinkLi = document.createElement('i');
LinkLi.setAttribute('class', 'fa fa-linkedin');
LiIcon.appendChild(linkLiClass);

socialIcons.appendChild(LiIcon);
LiIcon.appendChild(linkLiClass);
linkLiClass.appendChild(LinkLi);

//GitHub Icon
var linkGitClass = document.createElement('a');
linkGitClass.href = 'https://github.com/SanjulaD';
var LinkGit = document.createElement('i');
LinkGit.setAttribute('class', 'fa fa-github');
GitIcon.appendChild(linkGitClass);

socialIcons.appendChild(GitIcon);
GitIcon.appendChild(linkGitClass);
linkGitClass.appendChild(LinkGit);

footerApp.appendChild(footerDiv);

footerDiv.appendChild(socialIcons);